import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../contexts/AuthContext';
import toast from 'react-hot-toast';
import axios from 'axios';
import { 
  ShoppingCart, 
  Scan, 
  Plus, 
  Minus, 
  Trash2, 
  CreditCard, 
  DollarSign,
  Receipt,
  LogOut,
  History,
  Search
} from 'lucide-react';

const CashierDashboard = () => {
  const { user, logout } = useAuth();
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [barcode, setBarcode] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [paymentReceived, setPaymentReceived] = useState('');
  const [showReceipt, setShowReceipt] = useState(false);
  const [lastSale, setLastSale] = useState(null);
  const [showHistory, setShowHistory] = useState(false);
  const [salesHistory, setSalesHistory] = useState([]);
  const [showProductSearch, setShowProductSearch] = useState(false);
  const [adminOverride, setAdminOverride] = useState(false);
  const [overrideDiscount, setOverrideDiscount] = useState(0);
  const [manualPrice, setManualPrice] = useState('');
  const barcodeInputRef = useRef(null);

  // Admin override barcode (secret)
  const ADMIN_OVERRIDE_BARCODE = 'ADMIN_OVERRIDE_2024';

  useEffect(() => {
    fetchProducts();
    // Focus barcode input on component mount
    if (barcodeInputRef.current) {
      barcodeInputRef.current.focus();
    }
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('/api/products');
      setProducts(response.data.products || []);
    } catch (error) {
      toast.error('Failed to load products');
    }
  };

  const fetchSalesHistory = async () => {
    try {
      const response = await axios.get('/api/sales');
      setSalesHistory(response.data.sales || []);
    } catch (error) {
      toast.error('Failed to load sales history');
    }
  };

  const handleBarcodeSubmit = async (e) => {
    e.preventDefault();
    if (!barcode.trim()) return;

    // Check for admin override barcode
    if (barcode === ADMIN_OVERRIDE_BARCODE) {
      if (user.role === 'admin') {
        setAdminOverride(true);
        toast.success('Admin override activated');
      } else {
        toast.error('Access denied');
      }
      setBarcode('');
      if (barcodeInputRef.current) {
        barcodeInputRef.current.focus();
      }
      return;
    }

    try {
      const response = await axios.get(`/api/products/barcode/${barcode}`);
      const product = response.data;

      if (product.stock <= 0) {
        toast.error('Product is out of stock');
        return;
      }

      addToCart(product);
      setBarcode('');

      // Refocus barcode input
      if (barcodeInputRef.current) {
        barcodeInputRef.current.focus();
      }
    } catch (error) {
      if (error.response?.status === 404) {
        toast.error('Product not found - Use search to find manually');
        setShowProductSearch(true);
      } else {
        toast.error('Error scanning barcode');
      }
    }
  };

  const addToCart = (product, customPrice = null) => {
    const existingItem = cart.find(item => item.id === product.id);

    // Apply custom price if admin override is active
    const finalPrice = customPrice || (adminOverride && manualPrice ? parseFloat(manualPrice) : product.price);
    const productWithPrice = { ...product, price: finalPrice };

    if (existingItem) {
      if (existingItem.quantity >= product.stock) {
        toast.error('Cannot add more items than available in stock');
        return;
      }
      setCart(cart.map(item =>
        item.id === product.id
          ? { ...item, quantity: item.quantity + 1, price: finalPrice }
          : item
      ));
    } else {
      setCart([...cart, { ...productWithPrice, quantity: 1 }]);
    }

    if (adminOverride && (customPrice || manualPrice)) {
      toast.success(`Added ${product.name} with override price $${finalPrice}`);
    } else {
      toast.success(`Added ${product.name} to cart`);
    }

    // Reset manual price after adding
    setManualPrice('');
  };

  const updateCartQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
      return;
    }

    const product = products.find(p => p.id === productId);
    if (newQuantity > product.stock) {
      toast.error('Cannot add more items than available in stock');
      return;
    }

    setCart(cart.map(item =>
      item.id === productId
        ? { ...item, quantity: newQuantity }
        : item
    ));
  };

  const removeFromCart = (productId) => {
    setCart(cart.filter(item => item.id !== productId));
  };

  const clearCart = () => {
    setCart([]);
  };

  const calculateTotal = () => {
    const subtotal = cart.reduce((sum, item) => sum + (item.price * item.quantity), 0);
    const discountAmount = adminOverride ? (subtotal * (overrideDiscount / 100)) : 0;
    const afterDiscount = subtotal - discountAmount;
    const taxRate = 0.1; // 10% tax - should come from settings
    const tax = afterDiscount * taxRate;
    return {
      subtotal: subtotal.toFixed(2),
      discount: discountAmount.toFixed(2),
      tax: tax.toFixed(2),
      total: (afterDiscount + tax).toFixed(2)
    };
  };

  const handleCheckout = async () => {
    if (cart.length === 0) {
      toast.error('Cart is empty');
      return;
    }

    const totals = calculateTotal();
    const paymentAmount = parseFloat(paymentReceived) || 0;

    if (paymentMethod === 'cash' && paymentAmount < parseFloat(totals.total)) {
      toast.error('Insufficient payment amount');
      return;
    }

    setLoading(true);

    try {
      const saleData = {
        items: cart.map(item => ({
          product_id: item.id,
          quantity: item.quantity
        })),
        payment_method: paymentMethod,
        payment_received: paymentAmount,
        discount_amount: 0
      };

      const response = await axios.post('/api/sales', saleData);

      setLastSale(response.data);
      setShowReceipt(true);
      setShowCheckout(false);
      clearCart();
      setPaymentReceived('');

      toast.success('Sale completed successfully!');

      // Refresh products to update stock
      fetchProducts();
    } catch (error) {
      const message = error.response?.data?.error || 'Checkout failed';
      toast.error(message);
    } finally {
      setLoading(false);
    }
  };

  const filteredProducts = products.filter(product =>
    product.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    product.barcode?.includes(searchTerm) ||
    product.category?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const totals = calculateTotal();

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">POS System</h1>
              <p className="text-sm text-gray-600">Welcome, {user?.full_name || user?.username}</p>
            </div>
            <div className="flex items-center gap-4">
              <button
                onClick={() => {
                  setShowHistory(true);
                  fetchSalesHistory();
                }}
                className="btn btn-outline"
              >
                <History className="w-4 h-4" />
                History
              </button>
              <button onClick={logout} className="btn btn-secondary">
                <LogOut className="w-4 h-4" />
                Logout
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-6">
        <div className="pos-layout">
          {/* Main Scanning Section */}
          <div className="space-y-4">
            {/* Primary Barcode Scanner */}
            <div className="card" style={{ minHeight: '200px' }}>
              <h3 className="text-xl font-bold mb-6 text-center"> Tire Shop POS - Scan Items</h3>
              <form onSubmit={handleBarcodeSubmit} className="space-y-4">
                <div className="barcode-input">
                  <Scan className="barcode-icon" />
                  <input
                    ref={barcodeInputRef}
                    type="text"
                    value={barcode}
                    onChange={(e) => setBarcode(e.target.value)}
                    placeholder="Scan barcode or enter manually..."
                    className="form-input text-lg py-3"
                    style={{ fontSize: '18px', padding: '12px 12px 12px 40px' }}
                  />
                </div>
                <button type="submit" className="btn btn-primary w-full text-lg py-3">
                  <Plus className="w-5 h-5" />
                  Add to Cart
                </button>
              </form>

              {adminOverride && (
                <div className="mt-4 p-4 bg-yellow-100 border border-yellow-300 rounded">
                  <h4 className="font-semibold text-yellow-800 mb-2"> Admin Override Active</h4>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="form-label text-sm">Manual Price Override</label>
                      <input
                        type="number"
                        step="0.01"
                        value={manualPrice}
                        onChange={(e) => setManualPrice(e.target.value)}
                        placeholder="Enter custom price"
                        className="form-input"
                      />
                    </div>
                    <div>
                      <label className="form-label text-sm">Discount %</label>
                      <input
                        type="number"
                        step="0.1"
                        value={overrideDiscount}
                        onChange={(e) => setOverrideDiscount(parseFloat(e.target.value) || 0)}
                        placeholder="0"
                        className="form-input"
                      />
                    </div>
                  </div>
                  <button
                    onClick={() => {
                      setAdminOverride(false);
                      setOverrideDiscount(0);
                      setManualPrice('');
                    }}
                    className="btn btn-outline btn-sm mt-2"
                  >
                    Disable Override
                  </button>
                </div>
              )}
            </div>

            {/* Quick Actions */}
            <div className="card">
              <h4 className="font-semibold mb-3">Quick Actions</h4>
              <div className="grid grid-cols-2 gap-2">
                <button
                  onClick={() => setShowProductSearch(true)}
                  className="btn btn-outline"
                >
                  <Search className="w-4 h-4" />
                  Search Products
                </button>
                <button
                  onClick={() => {
                    setShowHistory(true);
                    fetchSalesHistory();
                  }}
                  className="btn btn-outline"
                >
                  <History className="w-4 h-4" />
                  History
                </button>
              </div>
            </div>

            {/* Instructions */}
            <div className="card bg-blue-50 border-blue-200">
              <h4 className="font-semibold text-blue-800 mb-2"> Instructions</h4>
              <ul className="text-sm text-blue-700 space-y-1">
                <li> Scan tire/part barcodes or enter manually</li>
                <li> Use "Search Products" if barcode not found</li>
                <li> Admin can scan override barcode for discounts</li>
                <li> Focus stays on scanner for quick entry</li>
              </ul>
            </div>
          </div>

          {/* Cart Panel */}
          <div className="cart-panel">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold flex items-center gap-2">
                <ShoppingCart className="w-5 h-5" />
                Cart ({cart.length})
              </h3>
              {cart.length > 0 && (
                <button onClick={clearCart} className="btn btn-outline btn-sm">
                  <Trash2 className="w-4 h-4" />
                  Clear
                </button>
              )}
            </div>

            <div className="cart-items">
              {cart.length === 0 ? (
                <p className="text-gray-500 text-center py-8">Cart is empty</p>
              ) : (
                cart.map(item => (
                  <div key={item.id} className="cart-item">
                    <div className="flex-1">
                      <h4 className="font-medium text-sm">{item.name}</h4>
                      <p className="text-xs text-gray-500">${item.price} each</p>
                    </div>
                    <div className="flex items-center gap-2">
                      <button
                        onClick={() => updateCartQuantity(item.id, item.quantity - 1)}
                        className="btn btn-outline btn-sm p-1"
                      >
                        <Minus className="w-3 h-3" />
                      </button>
                      <span className="w-8 text-center text-sm">{item.quantity}</span>
                      <button
                        onClick={() => updateCartQuantity(item.id, item.quantity + 1)}
                        className="btn btn-outline btn-sm p-1"
                      >
                        <Plus className="w-3 h-3" />
                      </button>
                      <button
                        onClick={() => removeFromCart(item.id)}
                        className="btn btn-danger btn-sm p-1 ml-2"
                      >
                        <Trash2 className="w-3 h-3" />
                      </button>
                    </div>
                    <div className="text-right ml-4">
                      <p className="font-medium">${(item.price * item.quantity).toFixed(2)}</p>
                    </div>
                  </div>
                ))
              )}
            </div>

            {cart.length > 0 && (
              <>
                <div className="cart-summary">
                  <div className="flex justify-between mb-2">
                    <span>Subtotal:</span>
                    <span>${totals.subtotal}</span>
                  </div>
                  <div className="flex justify-between mb-2">
                    <span>Tax (10%):</span>
                    <span>${totals.tax}</span>
                  </div>
                  <div className="flex justify-between cart-total">
                    <span>Total:</span>
                    <span>${totals.total}</span>
                  </div>
                </div>

                <button
                  onClick={() => setShowCheckout(true)}
                  className="btn btn-success w-full mt-4"
                >
                  <CreditCard className="w-4 h-4" />
                  Checkout
                </button>
              </>
            )}
          </div>
        </div>
      </div>

      {/* Checkout Modal */}
      {showCheckout && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3 className="modal-title">Checkout</h3>
              <button
                onClick={() => setShowCheckout(false)}
                className="modal-close"
              >

              </button>
            </div>

            <div className="space-y-4">
              <div>
                <h4 className="font-medium mb-2">Order Summary</h4>
                <div className="bg-gray-50 p-3 rounded">
                  {cart.map(item => (
                    <div key={item.id} className="flex justify-between text-sm">
                      <span>{item.name} x{item.quantity}</span>
                      <span>${(item.price * item.quantity).toFixed(2)}</span>
                    </div>
                  ))}
                  <div className="border-t pt-2 mt-2 font-medium">
                    <div className="flex justify-between">
                      <span>Total:</span>
                      <span>${totals.total}</span>
                    </div>
                  </div>
                </div>
              </div>

              <div className="form-group">
                <label className="form-label">Payment Method</label>
                <select
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  className="form-select"
                >
                  <option value="cash">Cash</option>
                  <option value="card">Card</option>
                  <option value="digital">Digital Payment</option>
                </select>
              </div>

              {paymentMethod === 'cash' && (
                <div className="form-group">
                  <label className="form-label">Amount Received</label>
                  <input
                    type="number"
                    step="0.01"
                    value={paymentReceived}
                    onChange={(e) => setPaymentReceived(e.target.value)}
                    placeholder="Enter amount received"
                    className="form-input"
                  />
                  {paymentReceived && (
                    <p className="text-sm text-gray-600 mt-1">
                      Change: ${Math.max(0, parseFloat(paymentReceived) - parseFloat(totals.total)).toFixed(2)}
                    </p>
                  )}
                </div>
              )}

              <div className="flex gap-2">
                <button
                  onClick={() => setShowCheckout(false)}
                  className="btn btn-outline flex-1"
                >
                  Cancel
                </button>
                <button
                  onClick={handleCheckout}
                  disabled={loading}
                  className="btn btn-success flex-1"
                >
                  {loading ? (
                    <>
                      <div className="spinner"></div>
                      Processing...
                    </>
                  ) : (
                    <>
                      <DollarSign className="w-4 h-4" />
                      Complete Sale
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Receipt Modal */}
      {showReceipt && lastSale && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3 className="modal-title">Receipt</h3>
              <button
                onClick={() => setShowReceipt(false)}
                className="modal-close"
              >

              </button>
            </div>

            <div className="receipt">
              <div className="receipt-header">
                <h3>Your Store Name</h3>
                <p>123 Main St, City, State 12345</p>
                <p>Tel: (555) 123-4567</p>
                <hr />
                <p>Sale ID: {lastSale.sale.id}</p>
                <p>Date: {new Date(lastSale.sale.created_at).toLocaleString()}</p>
                <p>Cashier: {lastSale.sale.cashier_name}</p>
              </div>

              <div className="receipt-items">
                {lastSale.items.map(item => (
                  <div key={item.id} className="receipt-item">
                    <div>
                      <div>{item.product_name}</div>
                      <div>{item.quantity} x ${item.unit_price}</div>
                    </div>
                    <div>${item.total_price}</div>
                  </div>
                ))}
              </div>

              <div className="receipt-total">
                <div className="receipt-item">
                  <span>Subtotal:</span>
                  <span>${lastSale.receipt_data.subtotal}</span>
                </div>
                <div className="receipt-item">
                  <span>Tax:</span>
                  <span>${lastSale.receipt_data.tax_amount}</span>
                </div>
                <div className="receipt-item">
                  <span><strong>Total:</strong></span>
                  <span><strong>${lastSale.receipt_data.total_amount}</strong></span>
                </div>
                <div className="receipt-item">
                  <span>Payment ({lastSale.sale.payment_method}):</span>
                  <span>${lastSale.receipt_data.payment_received}</span>
                </div>
                <div className="receipt-item">
                  <span>Change:</span>
                  <span>${lastSale.receipt_data.change_given}</span>
                </div>
              </div>

              <div className="text-center mt-4">
                <p>Thank you for your business!</p>
              </div>
            </div>

            <div className="flex gap-2 mt-4">
              <button
                onClick={() => window.print()}
                className="btn btn-primary flex-1"
              >
                <Receipt className="w-4 h-4" />
                Print Receipt
              </button>
              <button
                onClick={() => setShowReceipt(false)}
                className="btn btn-outline flex-1"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Sales History Modal */}
      {showHistory && (
        <div className="modal-overlay">
          <div className="modal-content" style={{ maxWidth: '800px' }}>
            <div className="modal-header">
              <h3 className="modal-title">Sales History</h3>
              <button
                onClick={() => setShowHistory(false)}
                className="modal-close"
              >

              </button>
            </div>

            <div className="overflow-x-auto">
              <table className="table">
                <thead>
                  <tr>
                    <th>Sale ID</th>
                    <th>Date</th>
                    <th>Total</th>
                    <th>Payment</th>
                  </tr>
                </thead>
                <tbody>
                  {salesHistory.map(sale => (
                    <tr key={sale.id}>
                      <td>#{sale.id}</td>
                      <td>{new Date(sale.created_at).toLocaleDateString()}</td>
                      <td>${sale.total_amount}</td>
                      <td className="capitalize">{sale.payment_method}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CashierDashboard;
